def translate_and_evaluate(input_sentence, reference_translation):
    prepared_data = clean_text(input_sentence)
    tokens = sp.encode(prepared_data, out_type=str)
    translation = translator.translate_batch([tokens],
                                             beam_size=1,
                                             disable_unk=True,
                                             use_vmap=True)
    translated_sentence = sp.decode(translation[0].hypotheses[0])
